bool palarell(Point& p1, Point& p2)
{
	return cross(p1, p2) == 0;
}

bool palarell(Point& p1_s, Point& p1_e, Point& p2_s, Point& p2_e)
{
	return palarell(p1_e - p1_s, p2_e - p2_s);
}

bool orthogonal(Point& p1, Point& p2)
{
	return dot(p1, p2) == 0;
}

bool orthogonal(Point& p1_s, Point& p1_e, Point& p2_s, Point& p2_e)
{
	return orthogonal(p1_e - p1_s, p2_e - p2_s);
}

Point projection(Point& hypo, Point& base)
{
	return base * (dot(hypo, base) / norm(base));
}

Point reflection(Point& hypo, Point& base)
{
	return base + (projection(hypo, base) - hypo) * 2.0;
}

