int projection(double xp1, double yp1, double xp2, double yp2, double* px, double* py)
{
    if (xp1 == xp2 && yp1 == yp2) {
        return 1;
    } else if (xp1 == xp2) {
        *px = xp1;
        return 0;
    } else {
        double dist = std::sqrt(std::pow((xp2 - xp1), 2) + std::pow((yp2 - yp1), 2));
        double cos = (xp2 - xp1) / dist;
        double sin = (yp2 - yp1) / dist;
        double xrot = cos * (*px) + sin * (*py);
        double yrot = -sin * (*px) + cos * (*py);
        yrot = 0;
        *px = cos * xrot - sin * yrot;
        *py = sin * xrot + cos * yrot;
        return 0;
    }
}


