struct point{
    double x,y;
};

int main(){
    //ios_base::sync_with_stdio(false);
    //cin.tie(NULL);

    point p1, p2;
    sf("%lf %lf %lf %lf",&p1.x,&p1.y,&p2.x,&p2.y);
    double A = p1.y-p2.y;
    double B = p2.x-p1.x;
    double C = A*p1.x+B*p1.y;
    int q;
    sf("%d",&q);
    while(q--){
        point p;
        sf("%lf %lf",&p.x,&p.y);
        double D = -B*p.x+A*p.y;
        double Det = A*A - (-B*B);
        double x = ( (A*C)-(B*D) ) /Det;
        double y = ( (A*D)-(-B*C) ) /Det;
        pf("%.10lf %.10lf\n",x,y);
    }

    return 0;
}

