bool equals(double x, double y, double eps=EPS)
{
    return fabs(x-y) < eps;
}

class Vec2d {
public:
    Vec2d() : x(0.0), y(0.0) {}
    Vec2d(double xx, double yy) : x(xx), y(yy) {}

    Vec2d& operator+=(const Vec2d& rhs)
    {
        x += rhs.x;
        y += rhs.y;
        return *this;
    }
    Vec2d& operator-=(const Vec2d& rhs)
    {
        x -= rhs.x;
        y -= rhs.y;
        return *this;
    }
    Vec2d& operator*=(double c)
    {
        x *= c;
        y *= c;
        return *this;
    }
    Vec2d& operator/=(double c)
    {
        x /= c;
        y /= c;
        return *this;
    }

    double norm() const { return x*x + y*y; }
    double abs() const { return sqrt(norm()); }

    double x;
    double y;
};

bool equals(const Vec2d& lhs, const Vec2d& rhs, double eps=EPS)
{
    return equals(lhs.x, rhs.x, eps) && equals(lhs.y, rhs.y, eps);
}

const Vec2d operator+(const Vec2d& lhs, const Vec2d& rhs) { return Vec2d(lhs) += rhs; }
const Vec2d operator-(const Vec2d& lhs, const Vec2d& rhs) { return Vec2d(lhs) -= rhs; }
const Vec2d operator*(const Vec2d& lhs, double rhs) { return Vec2d(lhs) *= rhs; }
const Vec2d operator*(double lhs, const Vec2d& rhs) { return Vec2d(rhs) *= lhs; }
const Vec2d operator/(const Vec2d& lhs, double rhs) { return Vec2d(lhs) /= rhs; }

ostream& operator<<(ostream& out, const Vec2d& v)
{
    return out << '(' << v.x << ',' << v.y << ')';
}

double dot(const Vec2d& lhs, const Vec2d& rhs)
{
    return lhs.x * rhs.x + lhs.y * rhs.y;
}

double cross(const Vec2d& lhs, const Vec2d& rhs)
{
    return lhs.x * rhs.y - lhs.y * rhs.x;
}

class Line {
public:
    Line() {}
    Line(const Vec2d& pp1, const Vec2d& pp2) : p1(pp1), p2(pp2) {}
    Line(double x1, double y1, double x2, double y2) : p1(x1,y1), p2(x2,y2) {}

    Vec2d base() const { return p2 - p1; }

    Vec2d p1;
    Vec2d p2;
};

bool is_orthogonal(const Vec2d& a, const Vec2d& b)
{
    return equals(dot(a,b), 0.0);
}

bool is_parallel(const Vec2d& a, const Vec2d& b)
{
    return equals(cross(a,b), 0.0);
}

Vec2d project(const Line& l, const Vec2d& p)
{
    Vec2d base = l.base();
    double r = dot(p - l.p1, base) / base.norm();
    return l.p1 + r * base;
}

Line L;

void solve()
{
    int Q; cin >> Q;

    REP(_, Q) {
        int x, y; cin >> x >> y;
        Vec2d p(x,y);
        Vec2d ans = project(L, p);
        PRINTLN(ans.x, ans.y);
    }
}

signed main(signed /*argc*/, char** /*argv*/)
{
    int x1, y1; cin >> x1 >> y1;
    int x2, y2; cin >> x2 >> y2;
    L = Line(x1, y1, x2, y2);

    solve();

    return 0;
}

