  int nq;
  cin >> nq;
  for (int i = 0; i < nq; i++) {
    int x, y;
    cin >> x >> y;
    auto ans = project(line, Vector(x, y));
    printf("%.10f %.10f\n", ans.x, ans.y);
  }
}

