// 2つのベクトルの角度
double angle(Vector a, Vector b) {
	double lena = abs(a), lenb = abs(b);
	if (lena == 0 || lenb == 0)return 0; //解なし
	double costheta = dot(a, b) / (lena*lenb);
	if (equals(costheta, 1))costheta = 1; //誤差対策
	return acos(costheta);
}

