PDD solve(double p1x, double p1y, double p2x, double p2y, double px, double py) {
	PDD re;

	if (p1x == p2x) {
		re = mp(p1x, py);
	}
	else if (p1y == p2y) {
		re = mp(px, p1y);
	}
	else {
		double l1s, l1y, l2s, l2y;
		l1s = (p1y - p2y) / (p1x - p2x);
		l1y = p1y + l1s * (p1x * -1);
		l2s = (1 / l1s)*-1;
		l2y = py + l2s * (px*-1);

		//cout << "line1 : y=" << l1s << "*x+" << l1y << endl;
		//cout << "line2 : y=" << l2s << "*x+" << l2y << endl;

		double ax, ay;
		ax = (l2y - l1y) / (l1s - l2s);
		ay = l1s * ax + l1y;
		re = mp(ax, ay);
	}

	return re;
}


