	Point operator -(Point p) { return Point(x - p.x, y - p.y);}
	Point operator *(double a) { return Point(a * x, a * y); }
	Point operator /(double a) { return Point(x / a, y / a); }
	inline double abs() { return sqrt(norm()); }
	inline double norm() { return x * x + y * y; }
	bool operator <(const Point& p)const {
		return x != p.x ? x < p.x : y < p.y;
	}
	bool operator ==(const Point& p)const {
		return fabs(x - p.x) < EPS && fabs(y - p.y) < EPS;
	}
};
typedef Point Vector;
class VectorOperation :public Point{//公有继承
public:
	double dot(Point a, Point b)//点乘
	{
		return a.x * b.x + a.y * b.y;
	}
	double cross(Point a, Point b)//叉乘
	{
		return a.x * b.y - a.y * b.x;
	}
};
class Line{
public:
	Point p1, p2;
public:
	Line() {};
	Line(int p1x, int p1y, int p2x, int p2y)
	{
		p1.x = p1x; p1.y = p1y;
		p2.x = p2x; p2.y = p2y;
	}
};
class LineOperation :public VectorOperation{
public:	
	LineOperation() {};
	bool isOrthogonal(Line s1, Line s2)
	{
		return equals(dot(s1.p2-s1.p1, s2.p2 - s2.p1), 0.0);//操作符重载的返回值的内存分配机制尚不可知，似乎返回值没有分配内存，dot的参数不能用引用，否则报错。
	}
	bool isParallel(Line s1, Line s2)
	{
		return equals(cross(s1.p2 - s1.p1, s2.p2 - s2.p1), 0.0);//外积判断平行，内积判断垂直。
	}
	Point projection(Line s, Point p)
	{
		Vector base = s.p1 - s.p2;
		double r = dot(p - s.p1, base) /base.norm();
		return s.p1 + base * r;
	}
	Point reflection(Line s, Point p)
	{
		return p + (projection(s, p) * 2.0);
	}
};
