using namespace std;

struct Vector2 {
    double x;
    double y;

    Vector2()
    : x(0.0)
    , y(0.0)
    {}

    Vector2(double _x, double _y) 
    : x(_x)
    , y(_y)
    {}

    double Length() const {
        return sqrt(x * x + y * y);
    }

    Vector2 Normalize() const {
        const double length = Length();
        Vector2 norm(x / length, y / length);
        return norm;
    }

    Vector2 operator+(const Vector2& vec) const {
        Vector2 v;
        v.x = x + vec.x;
        v.y = y + vec.y;
        return v;
    }

    Vector2 operator-(const Vector2& vec) const {
        Vector2 v;
        v.x = x - vec.x;
        v.y = y - vec.y;
        return v;
    }

    Vector2 operator*(const double d) const {
        Vector2 v;
        v.x = x * d;
        v.y = y * d;
        return v;
    }
};

double Dot(const Vector2& l, const Vector2& r) {
    return l.x * r.x + l.y * r.y;
}

int main() {
    int p0X, p0Y, p1X, p1Y;
    cin >> p0X >> p0Y >> p1X >> p1Y;

    int q;

    cin >> q;

    const Vector2 p0(p0X, p0Y);
    const Vector2 p1(p1X, p1Y);

    const Vector2 p0p1 = p1 - p0;

    while(q--) {
        int ptx, pty;
        cin >> ptx >> pty;

        const Vector2 pt(ptx, pty);
        const Vector2 p0pt = pt - p0;

        const Vector2 ans = p0 + p0p1 * (Dot(p0p1, p0pt) / (p0p1.Length() * p0p1.Length()));
        cout << fixed << setprecision(10) << ans.x << " " << ans.y << endl;
    }

    return 0;
}

