    segment S = {point(-20,0), point(-9,0)};
    assert(intersect(T, S).size() == 2);
  }
  {
    segment T = {point(-10,0), point(10,0)};
    segment S = {point( 20,0), point( 10,0)};
    assert(intersect(T, S).size() == 1);
    assert(intersect(intersect(T, S)[0], point( 10,0)).size() == 1);
  }
  {
    segment T = {point(-10,0), point(10,0)};
    segment S = {point( 20,0), point( 9,0)};
    assert(intersect(T, S).size() == 2);
  }
  {
    segment T = {point(-10,0), point(10,0)};
    segment S = {point( 5,0), point(-5,0)};
    assert(intersect(T, S).size() == 2);
  }
  {
    segment T = {point(-10,0), point(10,0)};
    segment S = {point(-50,0), point(50,0)};
    assert(intersect(T, S).size() == 2);
  }
}
