  srand(time(0));
  point r(1.0 - 2.0*rand()/(1.0+RAND_MAX),
          1.0 - 2.0*rand()/(1.0+RAND_MAX));
  r = r / abs(r);
  {
    circle C({r*point(0,0), 3.0});
    circle D({r*point(1,0), 1.0});
    auto v = tangent(C, D);
    assert(v.size() == 0);
    for (auto l: v) {
      assert(intersect(C, l).size() == 1);
      assert(intersect(D, l).size() == 1);
    }
