      assert(intersect(D, l).size() == 1);
    }
  }
  {
    circle C({r*point(0,0), 2.0});
    circle D({r*point(5,0), 2.0});
    auto v = tangent(C, D);
    assert(v.size() == 4);
    for (auto l: v) {
      assert(intersect(C, l).size() == 1);
      assert(intersect(D, l).size() == 1);
    }
  }
}
