const double EPS = 1e-10;
int dcmp(double x)
{
    if(fabs(x) < EPS) return 0;
    else return x < 0 ? -1 : 1;
}

bool operator == (const Point &a, const Point &b)
{
    return dcmp(a.x - b.x) == 0 && dcmp(a.y - b.y);
}

//向量点积
double Dot(Vector A, Vector B)
{
    return A.x * B.x + A.y * B.y;
}

//向量长度
double Length(Vector A)
{
    return sqrt(Dot(A, A));
}

//向量叉积
double Cross(Vector A, Vector B)
{
    return A.x * B.y - A.y * B.x;
}

//判断点是否在点段上，不包含端点
bool OnSegment(Point P, Point a1, Point a2)
{
    return dcmp(Cross(a1 - P, a2 - P) == 0 && dcmp((Dot(a1 - P, a2 - P)) < 0));
}

//点到直线距离
double DistanceToLine(Point P, Point A, Point B) 
{
    Vector v1 = B - A, v2 = P - A;
    return fabs(Cross(v1, v2) / Length(v1)); //不取绝对值，得到的是有向距离
}

//点在多边形内的判定
int isPointInPolygon(Point p, Point *poly, int n)
{
    int wn = 0;
    for(int i = 0; i < n; i++)
    {
        if(OnSegment(p, poly[i], poly[(i + 1) % n])) return -1; //在边界上
        int k = dcmp(Cross(poly[(i + 1) % n] - poly[i], p - poly[i]));
        int d1 = dcmp(poly[i].y - p.y);
        int d2 = dcmp(poly[(i + 1) % n].y - p.y);
        if(k > 0 && d1 <= 0 && d2 > 0) wn++;
        if(k < 0 && d2 <= 0 && d1 > 0) wn++;
    }
    if(wn != 0) return 1;       //内部
    return 0;                   //外部
}

//判断是否为凸多边形
bool isConvex(Point *poly, int n)
{
    bool s[3];
    memset(s, false, sizeof s);
    for(int i = 0; i < n; i++)
    {
        s[dcmp( Cross((poly[(i + 1) % n] - poly[i]), (poly[(i + 2) % n] - poly[i])) ) + 1] = true;
        if(s[0] && s[2])  
            return false;
    }
    return true;
}

bool LengthLong(int n, Point pa, double r)//点到线距离的最小值
{
    double ans = DistanceToLine(pa, P[0], P[1]);
    for(int i = 1; i < n - 1; i++)
    {
        ans = min(ans, DistanceToLine(pa, P[i], P[i + 1]));
    }
    if(dcmp(r-ans)<=0)
            return 1;
    return 0;
}

//点在直线上的投影
Point GetLineProjection(Point P, Point A, Point B) {
    Vector v = B - A;
    return A+v*(Dot(v, P-A)/Dot(v, v));
}

