inline int ccw(const Point &a, const Point &b, const Point &c) {
    D d = cross(b - a, c - a);
    if (sgn(d) > 0) {
        return +1; //anti-clockwise
    } else if (sgn(d) < 0) {
        return -1; //clockwise
    } else {
        if (sgn(dot(b - a, c - a)) < 0) {
            return -2;
        } else if (sgn(dot(a - b, c - b)) < 0) {
            return +2;
        }
    }
    return 0;
}
inline Point projection(const Point &p, const Point &a, const Point &b) { //a to b
    return p + (dot(a - p, b - p) / norm(a - p)) * (a - p);
}
inline Point reflection(const Point &p, const Point &a, const Point &b) {
    return b + (projection(p, a, b) - (b - p)) * static_cast<D>(2.0);
}
