// 2次元ベクトルクラス
class Vector {
public:
    float x, y;

    Vector() {
        x = 0.f;
        y = 0.f;
    }

    Vector(float nx, float ny) {
        x = nx;
        y = ny;
    }

    void set(float nx, float ny) {
        x = nx;
        y = ny;
    }

    void setX(float nx) {
        x = nx;
    }

    void setY(float ny) {
        y = ny;
    }

    // ベクトルの大きさを求める
    float getMagnitude() const {
        return sqrt(x * x + y * y);
    }

    // 内積を求める
    float getScalar(const Vector& vec) {
        return x * vec.x + y * vec.y;
    }

    // 角度を求める
    float getAngle(const Vector& vec) {
        return getScalar(vec) / (getMagnitude() * vec.getMagnitude());
    }

    // 単位ベクトルを返す
    Vector getUnit() {
        Vector v;
        float mag = getMagnitude();
        if (mag == 0.f) {
            v.set(0.f, 0.f);
        } else {
            v.set(x / mag, y / mag);
        }
        return v;
    }

    void operator = (const Vector& vec) {
        x = vec.x;
        y = vec.y;
    }

    Vector operator + (const Vector& vec) {
        Vector v(x + vec.x, y + vec.y);
        return v;
    }

    Vector operator - (const Vector& vec) {
        Vector v(x - vec.x, y - vec.y);
        return v;
    }

    Vector operator * (const Vector& vec) {
        Vector v(x * vec.x, y * vec.y);
        return v;
    }

    Vector operator * (float f) {
        Vector v(x * f, y * f);
        return v;
    }

    Vector operator / (const Vector& vec) {
        Vector v;
        if (vec.x == 0.f) {
            v.setX(0.f);
        } else {
            v.setX(x / vec.x);
        }

        if (vec.y == 0.f) {
            v.setY(0.f);
        } else {
            v.setY(y / vec.y);
        }
        return v;
    }

    Vector operator / (float f) {
        Vector v;
        if (f == 0.f) {
            v.set(0.f, 0.f);
        } else {
            v.set(x / f, y / f);
        }
        return v;
    }

    void operator += (const Vector& vec) {
        x += vec.x;
        y += vec.y;
    }

    void operator -= (const Vector& vec) {
        x -= vec.x;
        y -= vec.y;
    }

    void operator *= (const Vector& vec) {
        x *= vec.x;
        y *= vec.y;
    }

    void operator /= (const Vector& vec) {
        if (vec.x == 0.f) {
            x = 0.f;
        } else {
            x /= vec.x;
        }

        if (vec.y == 0.f) {
            y = 0.f;
        } else {
            y /= vec.y;
        }
    }
};


