    for(int i=0; i<q; i++){
        double x,y,a2,b2,c2;
        cin>>x>>y;
        vertical_straight_line(x, y, a, b, c, &a2, &b2, &c2);
        double ans_x, ans_y;
        intersection_and_parallel(a, b, c, a2, b2, c2, &ans_x, &ans_y);
        cout<<ans_x<<" "<<ans_y<<endl;
    }

}

