// 浮動小数点のゼロ判定
#define EPS (1e-10)
#define equals(a, b) ( fabs((a) - (b)) < EPS )

// 点を表すクラス
class Point
{
    public:
        double x, y;
        // コンストラクタ
        Point(double x = 0.0, double y = 0.0) : x(x), y(y){ }

        // -----▼▼▼ 演算子のオーバーロード ▼▼▼-----

        Point operator + (Point &p)
        {
            return Point(x + p.x, y + p.y);
        }

        Point operator - (Point &p)
        {
            return Point(x - p.x, y - p.y);
        }

        Point operator * (double a)
        {
            return Point(a * x, a * y);
        }

        Point operator / (double a)
        {
            return Point(x / a, y / a);
        }

        bool operator < ( const Point &p) const
        {
            return x != p.x ? x < p.x : y < p.y;
        }

        bool operator == ( const Point &p) const
        {
            return fabs(x - p.x) < EPS && fabs(y -p.y) < EPS;
        }

        // -----▲▲▲ 演算子のオーバーロード ▲▲▲-----

        // 2点間の距離
        double distance()
        {
            return sqrt( norm() );
        }

        double norm()
        {
            return ( x * x ) + ( y * y );
        }
};

// 線分を表すクラス
class Segment
{
    public:
        Point p1, p2;
        // コンストラクタ
        Segment(Point p1, Point p2) : p1(p1), p2(p2){ }
};

// 点とベクトルの表現は同じ
typedef Point Vector;
// 線分と直線の表現は同じ
typedef Segment Line;

double dot(Vector a, Vector b);
double cross(Vector a, Vector b);

int main(void)
{
    int q;
    Vector p1, p2, p;
    Vector vertical, base, OH;

    // ベクトル要素の格納
    cin >> p1.x >> p1.y >> p2.x >> p2.y;

    cin >> q;

    for(int i = 0; i < q; i++)
    {

        // ベクトル要素の格納
        cin >> p.x >> p.y;

        base = p2 - p1;
        vertical = p - p1;

        // 正射影ベクトルの考え方より
        // OHベクトル = ( ( a, bベクトルの内積 ) / aベクトルの大きさの2乗 ) * aベクトル
        OH = ( base * ( dot(base, vertical) / pow( base.distance(), 2.0 ) ) ) + p1;

         printf("%.10lf %.10lf\n", OH.x, OH.y);
    }

    return 0;
}

