    while(q--){
        Vec p; p.read();
        double dis = p.disToLine(p1,p2);
        int sgn = ((p2-p1)^(p-p1))>=0?-1:+1;
        //(p2-p1).rotate(acos(-1)/2).prt();
        p=p+(p2-p1).rotate(acos(-1)/2).change(dis)*sgn;
        p.prt();
    }
}

