using namespace std;
using NUM = double;

template<typename T>
struct Point {
	T x, y;
	Point<T> operator+(const Point<T>& rhs)const {
		Point<T> p;
		p.x = this->x + rhs.x;
		p.y = this->y + rhs.y;
		return p;
	}
	Point<T> operator-(const Point<T>& rhs)const {
		Point<T> p;
		p.x = this->x - rhs.x;
		p.y = this->y - rhs.y;
		return p;
	}
	Point<T> operator*(const T rhs) const {
		Point<T> p(*this);
		p.x = this->x * rhs;
		p.y = this->y * rhs;
		return p;
	}
};

template<typename T>
bool isZero(const T val)
{
	return fabs(val) < 1e-10;
}

template<typename T>
T innerProduct(const Point<T>& p0, const Point<T>& p1)
{
	double x0 = p0.x;
	double y0 = p0.y;
	double x1 = p1.x;
	double y1 = p1.y;
	return x0 * x1 + y0 * y1;
}

template<typename T>
T outerProduct(const Point<T>& p0, const Point<T>& p1)
{
	double x0 = p0.x;
	double y0 = p0.y;
	double x1 = p1.x;
	double y1 = p1.y;
	return x0 * y1 - y0 * x1;
}

template<typename T>
T norm(const Point<T>& p)
{
	double x = p.x;
	double y = p.y;
	return x*x + y*y;
}

template<typename T>
void readPoint(Point<T>& p)
{
	cin >> p.x;
	cin >> p.y;
}

template<typename T>
void outputComponentesOfPoint(const Point<T>& p)
{
	cout << fixed;
	cout << setprecision(8) << p.x;
	cout << " ";
	cout << setprecision(8) << p.y;
	cout << endl;
}

int main()
{

	Point<NUM> p1,p2;
	readPoint(p1);
	readPoint(p2);
	const Point<NUM> b = p2 - p1;
	const NUM b_b_ = norm(b);

	int q; cin >> q;
	for (int i = 0; i != q; ++i) {
		Point<NUM> p;
		readPoint(p);
		const Point<NUM> a = p - p1;
		const NUM ab = innerProduct(a, b);
		const Point<NUM> projectedPoint = p1 + b*(ab / b_b_);
		outputComponentesOfPoint(projectedPoint);
	};

	return 0;
}

