int main() {
    double x, y;
    scanf("%lf %lf", &x, &y);
    Point p1(x, y);

    scanf("%lf %lf", &x, &y);
    Point p2(x, y);

    if (real(p2) < real(p1))
        swap(p1, p2);

    Point e(real(p2)-real(p1), imag(p2)-imag(p1));
    e /= abs(e);

    size_t q;
    scanf("%zu", &q);
    for (size_t i=0; i<q; ++i) {
        double x, y;
        scanf("%lf %lf", &x, &y);
