#define EPS 1.0e-9

double dist(int x, int y)
{
	return sqrt(x * x + y * y);
}

double angle(int x, int y)
{
	return atan2((double)(x), (double)(y));
}

int main()
{
	int x0, y0, x1, y1, x2, y2, q;

	cin >> x0 >> y0 >> x1 >> y1 >> q;

	for (int i = 0; i < q; i++)
	{
		cin >> x2 >> y2;

		int ax = x1 - x0;
		int ay = y1 - y0;
		int bx = x2 - x0;
		int by = y2 - y0;

		double ad = dist(ax, ay);
		double bd = dist(bx, by);

		double at = angle(ax, ay);
		double bt = angle(bx, by);

		double cx = ax * (bd / ad) * cos(bt - at);
		double cy = ay * (bd / ad) * cos(bt - at);

		if (x0 + cx < -EPS || EPS < x0 + cx)
		{
			cout << setprecision(15) << x0 + cx << ' ';
		}
		else
		{
			cout << 0 << ' ';
		}

		if (y0 + cy < -EPS || EPS < y0 + cy)
		{
			cout << setprecision(15) << y0 + cy << endl;
		}
		else
		{
			cout << 0 << endl;
		}
	}

	return 0;
}

