using namespace std;

// 2次元ベクトルクラス
class Vector {
public:
    double x, y;

    Vector() {
        x = 0.f;
        y = 0.f;
    }

    Vector(double nx, double ny) {
        x = nx;
        y = ny;
    }

    void set(double nx, double ny) {
        x = nx;
        y = ny;
    }

    void setX(double nx) {
        x = nx;
    }

    void setY(double ny) {
        y = ny;
    }

    // ベクトルの大きさを求める
    double getMagnitude() const {
        return sqrt(x * x + y * y);
    }

    // 内積を求める
    double getScalar(const Vector& vec) {
        return x * vec.x + y * vec.y;
    }

    // 角度を求める
    double getAngle(const Vector& vec) {
        return getScalar(vec) / (getMagnitude() * vec.getMagnitude());
    }

    // 単位ベクトルを返す
    Vector getUnit() {
        Vector v;
        double mag = getMagnitude();
        if (mag == 0.f) {
            v.set(0.f, 0.f);
        } else {
            v.set(x / mag, y / mag);
        }
        return v;
    }

    void operator = (const Vector& vec) {
        x = vec.x;
        y = vec.y;
    }

    Vector operator + (const Vector& vec) {
        Vector v(x + vec.x, y + vec.y);
        return v;
    }

    Vector operator - (const Vector& vec) {
        Vector v(x - vec.x, y - vec.y);
        return v;
    }

    Vector operator * (const Vector& vec) {
        Vector v(x * vec.x, y * vec.y);
        return v;
    }

    Vector operator * (double f) {
        Vector v(x * f, y * f);
        return v;
    }

    Vector operator / (const Vector& vec) {
        Vector v;
        if (vec.x == 0.f) {
            v.setX(0.f);
        } else {
            v.setX(x / vec.x);
        }

        if (vec.y == 0.f) {
            v.setY(0.f);
        } else {
            v.setY(y / vec.y);
        }
        return v;
    }

    Vector operator / (double f) {
        Vector v;
        if (f == 0.f) {
            v.set(0.f, 0.f);
        } else {
            v.set(x / f, y / f);
        }
        return v;
    }

    void operator += (const Vector& vec) {
        x += vec.x;
        y += vec.y;
    }

    void operator -= (const Vector& vec) {
        x -= vec.x;
        y -= vec.y;
    }

    void operator *= (const Vector& vec) {
        x *= vec.x;
        y *= vec.y;
    }

    void operator /= (const Vector& vec) {
        if (vec.x == 0.f) {
            x = 0.f;
        } else {
            x /= vec.x;
        }

        if (vec.y == 0.f) {
            y = 0.f;
        } else {
            y /= vec.y;
        }
    }
};


int main() {
    Vector vec1, vec2;
    double x, y;
    cin >> x;
    cin >> y;
    vec1.set(x,y);
    cin >> x;
    cin >> y;
    vec2.set(x,y);

    int n;
    cin >> n;

    Vector vec = vec2 - vec1;
    Vector p, ret;
    vector<Vector> results;
    for (int i = 0; i < n; i++) {
        double px, py;
        cin >> px;
        cin >> py;
        p.set(px, py);
        p -= vec1;

        double cos = p.getAngle(vec);
        double size = p.getMagnitude() * cos;
        ret = vec.getUnit() * size;

        results.push_back(ret);
    }

    for (int i = 0; i < results.size(); i++) {
        cout << results[i].x << " " << results[i].y << endl;
     }
    return 0;
}

