}

#include <bits/stdc++.h>
using namespace std;

#define db double

struct Poi {
	db x, y;
	void r() { cin >> x; cin >> y; }
	void w() { cout << x << ' ' << y << '\n'; }
	Poi operator -(Poi p) {
		return {x - p.x, y - p.y};
	}
	Poi operator +(Poi p) {
		return {x + p.x, y + p.y};
	}
	Poi operator *(db d) {
		return {x * d, y * d};
	}
	db dot(Poi p) {
		return x * p.x + y * p.y;
	}
	db len2() { return x * x + y * y; }
	Poi proj(Poi p, Poi q) {
		db s = (*this - p).dot(q - p) / (q - p).len2();
		return p + (q - p) * s;
	}
};

int main() {
	ios :: sync_with_stdio(false);
	cout << fixed << setprecision(12);

	Poi p, q; p.r(); q.r();
	int T; cin >> T;
	while(T --) {
		Poi t; t.r();
		t.proj(p, q).w();
	}
	return 0;
}

