using namespace std;
class Vector
{
public:
	double x;
	double y;
	Vector(double x=0,double y=0):x(x),y(y){}
	Vector operator +(Vector &p)
	{
		return Vector(x + p.x, y + p.y);
	}
	Vector operator -(Vector &p)
	{
		return Vector(x - p.x, y - p.y);
	}
	Vector operator *(double k)
	{
		return Vector(k*x,k*y);
	}
	double abs(Vector &p)
	{
		return sqrt(p.x*p.x + p.y*p.y);
	}
	double dot(Vector &p1, Vector &p2)
	{
		return p1.x*p2.x + p1.y*p2.y;
	}
	Point projection(Point p1, Point p2)
	{
		Vector p1p(x - p1.x, y - p1.y);
		Vector p1p2(p2.x - p1.x, p2.y - p1.y);
		double pj = dot(p1p, p1p2) / abs(p1p2);
		Vector p1x = p1p2 * (pj / abs(p1p2));
		Point x = p1x + p1;
		return x;
	}
};
