using namespace std;

long projection(double xp1, double yp1, double xp2, double yp2, double x, double y, double* px, double* py)
{
    if ( xp1 == xp2 && yp1 == yp2 ) {
        return 1;
    } else if ( xp1 == xp2 ) {
        *px = xp1;
        *py = y;
        return 0;
    } else {
        double dDist = sqrt(pow((xp2 - xp1), 2) + pow((yp2 - yp1), 2));
        double dCos = (xp2 - xp1) / dDist;
        double dSin = (yp2 - yp1) / dDist;
        double dXrot = dCos * (x - xp1) + dSin * (y - yp1);
        double dYrot = -dSin * (x - xp1) + dCos * (y - yp1);
        dYrot = 0;
        *px = dCos * dXrot - dSin * dYrot + xp1;
        *py = dSin * dXrot + dCos * dYrot + yp1;
        return 0;
    }
}


