	Vector2 base = vecs[1] - vecs[0];
	base.SetVector2(base.GetX() / Norm(base), base.GetY() / Norm(base));

	cin >> query;

	for (int i = 0; i < query; i++) {
		Vector2 vecAns;
		double x, y;
		cin >> x >> y;
		vecAns.SetVector2(x, y);

		Vector2 ans = base * Dot(base, vecAns);
		cout << ans.GetX() << " " << ans.GetY() << endl;
	}

	return 0;
}

