        : x {in_x}, y {in_y} {};
    Vec2(const Vec2&) = default;
    Vec2(Vec2&&) = default;

    Vec2 normalized() {
        double mag = Magnitude(*this);
        return {x /= mag, y /= mag};
    }

    Vec2 operator-() {
        return {-x, -y};
    }

    Vec2 operator+(const Vec2& rhs) {
        return {x + rhs.x, y + rhs.y};
    }

    Vec2 operator-(const Vec2& rhs) {
        return {x - rhs.x, y - rhs.y};
    }

    Vec2 operator*(double rhs) {
        return {x * rhs, y * rhs};
    }

    Vec2 operator/(double rhs) {
        return {x / rhs, y / rhs};
    }

    Vec2& operator+=(const Vec2& rhs) {
        x += rhs.x;
        y += rhs.y;
        return *this;
    }

    Vec2& operator-=(const Vec2& rhs) {
        x -= rhs.x;
        y -= rhs.y;
        return *this;
    }

    Vec2& operator*=(double rhs) {
        x *= rhs;
        y *= rhs;
        return *this;
    }

    Vec2& operator/=(double rhs) {
        x /= rhs;
        y /= rhs;
        return *this;
    }
};

