double vecsum(const double v0[2], const double v1[2])
{
    return v0[0] * v1[0] + v0[1] * v1[1];
}

double veclen(const double v[2])
{
    return sqrt(v[0] * v[0] + v[1] * v[1]);
}

bool vecnrm(double eps, const double v0[2], double v1[2])
{
    double len = veclen(v0);
    if (len < eps) {
        return false;
    }
    vecscl(1 / len, v0, v1);
    return true;
}

const double eps = 1e-12;

void solve()
{
    int x0, y0, x1, y1;
    scanf("%d%d%d%d", &x0, &y0, &x1, &y1);
    double p0[2] = { (double)x0, (double)y0 };
    double v0[2] = { (double)x1 - x0, (double)y1 - y0 };
    bool ok = vecnrm(eps, v0, v0);
    assert(ok);

    int Q;
    scanf("%d", &Q);
    int t;
    for (t = 0; t < Q; t++) {
        int x, y;
        scanf("%d%d", &x, &y);
        double v[2] = { (double)x, (double)y };
        double vtmp[2], vtmp2[2], vtmp3[2];
        vecsub(v, p0, vtmp);
        double val = vecsum(v0, vtmp);
        vecscl(val, v0, vtmp2);
        vecadd(p0, vtmp2, vtmp3);
        printf("%.10lf %.10lf\n", vtmp3[0], vtmp3[1]);
    }

    return;
}


int main(int argc, char* argv[])
{
#if 1
    solve();
#else
    int T;
    scanf("%d", &T);
    int t;
    for(t=0; t<T; t++) {
        //printf("Case #%d: ", t+1);
        solve();
    }
#endif
    return 0;
}

