#include <bits/stdc++.h>
using namespace std;

const double eps = 1e-9;

bool same(double a, double b) { return abs(a - b) < eps; }

struct P {
    double x, y;
    P() : x(0), y(0) {}
    P(double x, double y) : x(x), y(y) {}
    P operator + (P b) { return P(x + b.x, y + b.y); }
    P operator - (P b) { return P(x - b.x, y - b.y); }
    P operator * (double b) { return P(x * b, y * b); }
    P operator / (double b) { return P(x / b, y / b); }
    double operator * (P b) { return x * b.x + y * b.y; }
    double operator ^ (P b) { return x * b.y - y * b.x; }
    double abs() { return hypot(x, y); }
    P unit() { return *this / abs(); }
};

struct L{
    // ax + by + c = 0
    double a, b ,c, o;
    P pa, pb;
    L() : a(0), b(0), c(0), o(0), pa(), pb() {}
    L(P pa, P pb) : a(pa.y - pb.y), b(pb.x - pa.x), c(pa ^ pb), o(atan2(-a, b)), pa(pa), pb(pb) {}
    P project(P p) {
        return pa + (pb - pa).unit() * ((pb - pa) * (p - pa) / (pb - pa).abs());
    }
};

P intersect(L x, L y) {
    if (same(x.a * y.b, x.b * y.a)) throw;
    double z = -x.a * y.b + x.b * y.a;
    return P(-x.b * y.c + x.c * y.b, x.a * y.c - x.c * y.a) / z;
}

int main() {
    P p1; cin >> p1.x >> p1.y;
    P p2; cin >> p2.x >> p2.y;
    L l(p1, p2);
    int q; cin >> q; while (q--) {
        P p; cin >> p.x >> p.y;
        p = l.project(p);
        cout << fixed << setprecision(10) << p.x << " " << p.y << '\n';
    }
}

