    return p;
}

struct Vector{ double vx, vy; };

Vector set_v(const Point &p1, const Point &p2)
{
    Vector v;
    v.vx = p2.x - p1.x;
    v.vy = p2.y - p1.y;
    return v;
}

double dist(const Point &p1, const Point &p2)
{
    return sqrt((p1.x - p2.x) * (p1.x - p2.x) + (p1.y - p2.y) * (p1.y - p2.y));
}

double norm(const Vector &v)
{
    return (v.vx) * (v.vx) + (v.vy) * (v.vy);
}

double inpro(const Vector &v1, const Vector &v2)
{
    return (v1.vx * v2.vx) + (v1.vy * v2.vy);
}

Point move(const Point &p, const Vector &v, const double t)
{
    Point moved_p;
    moved_p.x = p.x + t * v.vx;
    moved_p.y = p.y + t * v.vy;
    return moved_p;
}

Point horizontal(Point &p1, Point &p2, Point &p3)
{
    Vector v2 = set_v(p1, p2);
    Vector v3 = set_v(p1, p3);
    double t;
    t = inpro(v2, v3) / norm(v2);
    Point p = move(p1, v2, t);
    return p;
}

