//verify
int Circles_Intersection(point a,double r1,point b,double r2){
	double dp =abs(a-b);//中心間の距離
	double dr =r1+r2;//円の半径の合計
	if(dp >dr){
		return 0;
	}else if(dp +r1 <r2){
		return -2;
	}else if(dp +r2 <r1){
		return 2;
	}else if(dp <=dr){
		return 1;
	}
	return -100;
}
point projection(point p1, point p2, point p3) //直線p1p2 上への写像
{
