  }
};
typedef Point Vector;  // ベクトルを表す構造体（成分表示）

double dot(Vector a, Vector b) {  // ベクトルの内積
  return a.x*b.x + a.y*b.y;
}
double cross(Vector a, Vector b) {  // ベクトルの外積
  return a.x*b.y - a.y*b.x;
}

struct Segment {Point p1, p2;};  // 線分を表す構造体(p1:始点、p2:終点)
typedef Segment Line;  // 直線を表す構造体（直線は無限長にする必要あり）

// 直交判定
bool isOrthogonal(Vector a, Vector b) {  // ベクトル同士の直交判定
  return equals(dot(a, b), 0.0);
}
bool isOrthogonal(Point a1, Point a2, Point b1, Point b2) {  // a1とa2、b1とb2をそれぞれ端点にもつ線分の直交判定
  return isOrthogonal(a1-a2, b1-b2);
}
bool isOrthogonal(Segment s1, Segment s2) {  // 線分s1、s2の直交判定
  return equals(dot(s1.p2-s1.p1, s2.p2-s2.p1), 0.0);
}

// 平行判定
bool isParallel(Vector a, Vector b) {  // ベクトル同士の平行判定
  return equals(cross(a, b), 0.0);
}
bool isParallel(Point a1, Point a2, Point b1, Point b2) {  // a1とa2、b1とb2をそれぞれ端点にもつ線分の平行判定
  return isParallel(a1-a2, b1-b2);
}
bool isParallel(Segment s1, Segment s2) {  // 線分s1、s2の平行判定
  return equals(cross(s1.p2-s1.p1, s2.p2-s2.p1), 0.0);
}

// 線分sに対する点pの射影
Point project(Segment s, Point p) {
  Vector base = s.p2 - s.p1;
  double r = dot(p - s.p1, base) / base.norm();
  return s.p1 + base * r;
}

