template<class T>
struct mycomplex{
    T real;
    T imag;

    mycomplex(T x=0, T y=0) : real(x), imag(y) {}

    mycomplex<T> conj(){
        return mycomplex<T>{real, -imag};
    }

    T abs(){
        return sqrt(real*real + imag*imag);
    }

    T norm(){
        return (real*real + imag*imag);
    }

    mycomplex operator-() const {return mycomplex<T>(-real,-imag);}
    mycomplex& operator+=(const mycomplex<T> a){
        real += a.real; imag += a.imag;
        return *this;
    }
    mycomplex& operator-=(const mycomplex<T> a){
        real -= a.real; imag -= a.imag;
        return *this;
    }
    mycomplex& operator*=(const mycomplex<T> a){
        T x = a.real * real - a.imag * imag;
        T y = a.imag * real + a.real * imag;
        real = x; imag = y;
        return *this;
    }
    mycomplex& operator*=(const T a){
        real *= a;
        imag *= a;
        return *this;
    }

    mycomplex operator+(const mycomplex<T> a) const { return mycomplex<T>(*this) += a; }
    mycomplex operator-(const mycomplex<T> a) const { return mycomplex<T>(*this) -= a; }
    mycomplex operator*(const mycomplex<T> a) const { return mycomplex<T>(*this) *= a; }
    mycomplex operator*(const T a) const { return mycomplex<T>(*this) *= a; }

};

