void Projection(Point P1, Point P2, Point P, Point Output){
    double ax = P2.getX() - P1.getX();
    double ay = P2.getY() - P1.getY();
    double bx = P.getX() - P1.getX();
    double by = P.getY() - P1.getY();
    double dot = ax*bx + ay*by;
    double length = ax*ax + ay*ay;

    double outX = ax * dot/length;
    double outY = ay * dot/length;

    Output.setPoint(outX, outY);
    Output.disp();
}


