    P tl = l.q - l.p;  //表示l的两个点形成的线段
    db ll = tl.len();  //线段长度
    db k = (a-l.p).dot(tl)/ll/ll;  //投影长度与线段长度的比值
    return P( l.p.x + k*tl.x, l.p.y + k*tl.y ); //最终结果
}

int main(){
    //freopen("owo.in","r",stdin);
    int n;
    L l;
    scanf("%lf%lf%lf%lf",&l.p.x, &l.p.y, &l.q.x, &l.q.y);
    cin >> n;
    P a;
    while(n--){
        scanf("%lf%lf", &a.x, &a.y);
        P ta = projection(a, l);
        printf("%.9lf %.9lf\n", ta.x, ta.y);
    }
    return 0;
}

