#define Vector2D Vertex2D

//単位ベクトル生成
Vector2D ceate_unit_vector( Vector2D v )
{
    long double len = pow( ( v.x * v.x ) + ( v.y * v.y ), 0.5 );//ベクトル長さ

    Vector2D ret;
    ret.x = 1.0000000000*v.x / len;
    ret.y = 1.0000000000*v.y / len;

    return ret;
}

//ベクトル内積
long double dot_product(Vector2D vl, Vector2D vr) {
    return 1.000000000*vl.x * vr.x + vl.y * vr.y;
}

//点Pと直線ABから線上最近点を求める
Vector2D NearPosOnLine(Vertex2D hoge, Vertex2D A, Vertex2D B )
{
    Vector2D AB,AP;//ベクトルAB AP

    AB.x = B.x - A.x;
    AB.y = B.y - A.y;
    AP.x = hoge.x - A.x;
    AP.y = hoge.y - A.y;

    //ABの単位ベクトルを計算
    Vector2D nAB = ceate_unit_vector(AB);

    //Aから線上最近点までの距離（ABベクトルの後ろにあるときはマイナス値）
    long double dist_AX = dot_product( nAB, AP );

    //線上最近点
    Vector2D ret;
    ret.x = A.x + (1.00000000 * nAB.x * dist_AX );
    ret.y = A.y + (1.00000000 *  nAB.y * dist_AX );

    return ret;
}
