    for (int ph = 0; ph < q; ph++) {
        cin >> x >> y;
        P p3 = P(x, y);
        P z;
        crossLL(L(p1, p2), L(p3, p3 + (p2-p1)*P(0, 1)), z);
        cout << z.x << " " << z.y << endl;
    }
    return 0;
}

