Point intersectionLs(Point a1, Point a2, Point b1, Point b2) {
    Point b = b2-b1;
    double d1 = abs(cross(b, a1-b1));
    double d2 = abs(cross(b, a2-b1));
    double t = d1 / (d1 + d2);

    return a1 + (a2-a1) * t;
}

