int main(void){

  pair<double, double> p0, p1, p;
  pair<double, double> ans;
  int n;
  cin >> p0.first >> p0.second >> p1.first >> p1.second;
  cin >> n;

  while(n--){
    cin >> p.first >> p.second;
    double a;
    if(p1.second - p0.second == 0){
      ans.first = p.first;
      ans.second = p1.second;
    }else if(p1.first - p0.first == 0){
      ans.first = p1.first;
      ans.second = p.second;
    }else{
      a = (p1.second - p0.second) / (p1.first - p0.first);
      ans.first = (a / (a*a + 1) ) * (-p0.second + p.second + a*p0.first + p.first/a);
      ans.second = (-1/a) * (ans.first - p.first) + p.second;
    }
    printf("%.10f ", ans.first);
    printf("%.10f", ans.second);
    cout << endl;
  }

  return 0;
}

#include<cstdio>
#define SQ(x) ((x)*(x))
int x1, y1, x2, y2;
void solve(int x, int y){
	const int x2m1 = x2 - x1;
	const int y2m1 = y2 - y1;
	int xm1 = x-x1;
	int ym1 = y-y1;
	int x2m = x2-x;
	int y2m = y2-y;
	double temp1 = SQ(xm1)+SQ(ym1)+SQ(x2m1)+SQ(y2m1)-SQ(x2m)-SQ(y2m);
	double temp2 = SQ(x2m1)*2+SQ(y2m1)*2;
	double res = temp1/temp2;
	printf("%.10f %.10f\n", (x2m1*res+x1), (y2m1*res+y1));
}
int main(){
	int x, y, q;
	scanf("%d%d%d%d%d", &x1, &y1, &x2, &y2, &q);
	while(q--){
		scanf("%d%d", &x,&y);
		solve(x, y);
	}
}

