	Point p1, p2;
	Segment(Point p1, Point p2): p1(p1), p2(p2) {};

	Point project(Point p) {
		Vector a = Vector(p.x - p1.x, p.y - p1.y);
		Vector b = Vector(p2.x - p1.x, p2.y - p1.y);
		return p1 + p2 * (dot(a, b) / b.norm());
	}
};

int main() {
	int x0, y0, x1, y1, q;
	cin >> x0 >> y0 >> x1 >> y1 >> q;
	Segment s = Segment(Point(x0, y0), Point(x1, y1));
	int x2, y2;
	for (int i = 0; i < q; i++) {
		cin >> x2 >> y2;
		Point pro = s.project(Point(x2, y2));
		cout << fixed << setprecision(10) << pro.x << ' ' << pro.y << endl;
	}
	return 0;
}

