    double x,y;
    Point(){}
    Point(double x,double y):x(x),y(y){
    }
    Point operator+ (Point p){
        return Point(add(x,p.x),add(y,p.y));
    }
    Point operator -(Point p){
        return Point(add(x,-p.x),add(y,-p.y));
    }
    Point operator *(double d){
        return Point(x*d,y*d);
    }
    double dot(Point p){//外积 p1.p2  outside
        return add(x*p.x,y*p.y);
    }
    double det(Point p){//内积 p1*p2    inner
        return add(x*p.y,-y*p.x);
    }
    double len(){
        return sqrt(add(x*x,y*y));
    }
};
Point operator *(double d,Point p){
    return Point(p.x*d,p.y*d);
}
Point Projection(Point p1,Point p2,Point p){//p 在线段p1,p2上的投影坐标
    double d1=1.0/(p2-p1).len();
    Point ans=(p-p1).dot((p2-p1))*(p2-p1)*d1*d1;
    return ans+p1;
}
typedef Point P;
int main(){
    P p1,p2;
    scanf("%lf%lf%lf%lf",&p1.x,&p1.y,&p2.x,&p2.y);
    int q;
    scanf("%d",&q);
    while(q--){
        P p;
        scanf("%lf%lf",&p.x,&p.y);
        P ans=Projection(p1,p2,p);
        printf("%.10f %.10f\n",ans.x,ans.y);
    }
    return 0;
}

