  TPoint operator+ (const TPoint p)
  {
    return TPoint(x + p.x , y + p.y);
  }

  TPoint operator- (const TPoint p)
  {
    return TPoint( p.x - x , p.y - y );
  }

  TPoint operator* (double p)
  {
    return TPoint(x * p, y * p);
  }

  double operator* (TPoint p)
  {
    return x * p.x + y * p.y;
  }

  double operator% (const TPoint p)
  {
    return (x * p.y) - ( y * p.x);
  }

  double operator~ ()
  {
    return x * x + y * y;
  }
};

TPoint projection(TPoint a, TPoint b, TPoint p)
{
  double t = (p-a)*(b-a) / (~(b-a));
  return (a-b)*t;
}

