	scanf("%d",&q);
	rep(i,q){
		D x,y;
		scanf("%Lf%Lf",&x,&y);
		P p=perp(a,P(x,y));
		printf("%.10Lf %.10Lf\n",p.real(),p.imag());
	}
	return 0;
}

