struct point_t {
	double x, y;
};

// ax + by + c = 0
struct line_t {
	double a, b, c;
};

point_t cross_point(line_t l, line_t m) {
	point_t p = { INFD, INFD };
	double d = l.a * m.b - m.a * l.b;
	if (nearlyeq(d, 0)) return p;
	double x = l.b * m.c - m.b * l.c;
	double y = l.a * m.c - m.a * l.c;
	p = { x / d, y / -d };
	return p;
}

// if a vector is (dx, dy), then a = dy, b = -dx
line_t solve_line(double a, double b, point_t p) {
	return { a, b, -a * p.x - b * p.y };
}

