template <typename T>
struct point {
    T x;
    T y;

    point(T x = 0, T y = 0) : x(x), y(y) {}

    friend ostream& operator<<(ostream& os, const point p) {
        os << p.x << " " << p.y;
        return os;
    }
};

template <typename T>
struct vec {
    T x;
    T y;

    vec(T x, T y) : x(x), y(y) {}

    vec(point<T> a) {
        x = a.x;
        y = a.y;
    }

    vec(point<T> a, point<T> b)  {
        x = b.x - a.x;
        y = b.y - a.y;
    }

    double magnitude() {
        return sqrt(x * x + y * y);
    }
};

template <typename T>
vec<T> scale(vec<T> v, double mag) {
    return vec<T>(v.x * mag, v.y * mag);
}

template <typename T>
T dot_product(vec<T> u, vec<T> v) {
    return u.x * v.x + u.y * v.y;
}

template <typename T>
point<T> translate(point<T> p, vec<T> v) {
    return point<T>(p.x + v.x, p.y + v.y);
}

int main() {

    double x1, y1, x2, y2;
    cin >> x1 >> y1 >> x2 >> y2;

    point<double> p1(x1, y1);
    point<double> p2(x2, y2);

    vec<double> v(p1, p2);

    int n;
    cin >> n;
    for (int i = 0; i < n; i++) {
        double x, y;
        cin >> x >> y;

        point<double> p(x, y);

        vec<double> u(p1, p);

        double len = dot_product(u, v) / v.magnitude() / v.magnitude();

        vec<double> vec_q = scale(v, len);

        point<double> q(vec_q.x, vec_q.y);

        point<double> ans = translate(q, vec<double>(point<double>(0, 0), p1));

        cout << ans << endl;
    }

    return 0;
}

