double dot(Point a,Point b){
    return real(a)*real(b)+imag(a)*imag(b);
}

double cross(Point a,Point b){
    return real(a)*imag(b)-imag(a)*real(b);
}

bool isOrthogonal(Point a,Point b){
    return EQ(dot(a,b),0.0);
}

bool isOrthogonal(Point a1,Point a2,Point b1,Point b2){
    return isOrthogonal(a1-a2,b1-b2);
}

bool isOrthogonal(Line l1,Line l2){
    return isOrthogonal(l1.p2-l1.p1,l2.p2-l2.p1);
}

bool isParallel(Point a,Point b){
    return EQ(cross(a,b),0.0);
}

bool isParallel(Point a1,Point a2,Point b1,Point b2){
    return isParallel(a1-a2,b1-b2);
}

bool isParallel(Line l1,Line l2){
    return isParallel(l1.p2-l1.p1,l2.p2-l2.p1);
}

Point project(Line l,Point p){
    Point base=l.p2-l.p1;
    double r=dot(p-l.p1,base)/norm(base);
    return l.p1+base*r;
}

Point reflect(Line l,Point p){
    return p+(project(l,p)-p)*2.0;
}
