using namespace std;

typedef complex<double> P;

#define EPS 1e-9
#define shosu(x) fixed<<setprecision(x)

struct L : public vector<P> {
  L(const P &a, const P &b) {
    push_back(a); push_back(b);
  }
};
struct C{
  P c;double r;
  C(const P &c,double r):c(c),r(r){}
};

namespace std{//演算子の定義
  bool operator < (const P& a,const P& b){
    return real(a)!= real(b) ? real(a) < real(b) : imag(a) < imag(b);
    //return imag(a) != imag(b) ? imag(a) < imag(b) : real(a) < real(b)
  }
  bool operator == (const P& a,const P& b){
    return a.real()==b.real() && a.imag()==b.imag();
  }
}

double dot(P a,P b){
  return real(conj(a)*b);
}

double cross(P a,P b){
  return imag(conj(a)*b);
}

int ccw(P a, P b, P c) {//3点の関係性
  b -= a; c -= a;
  if (cross(b, c) > 0)   return +1;       // counter clockwise
  if (cross(b, c) < 0)   return -1;       // clockwise
  if (dot(b, c) < 0)     return +2;       // c--a--b on line
  if (norm(b) < norm(c)) return -2;       // a--b--c on line
  return 0;                               // a--c--b on line
}

P projection(L l, P p){//pの直線l上の射影
  double t=dot(p-l[0],l[0]-l[1])/norm(l[0]-l[1]);
  return l[0]+t*(l[0]-l[1]);
}

P reflection(L l, P p){//点pの直線lに関して対称な点
  return p+2.0*(projection(l,p)-p);
}

bool isOrthogonal(L l1, L l2){//2直線の直交判定
  return fabs(dot(l1[1]-l1[0],l2[1]-l2[0])) < EPS;
}

bool isParallel(L l1, L l2){//2直線の平行判定
  return fabs(cross(l1[1]-l1[0],l2[1]-l2[0])) < EPS;
}

int main(){
  double xp1,yp1,xp2,yp2;
  cin>>xp1>>yp1>>xp2>>yp2;
  P p1(xp1,yp1), p2(xp2,yp2);
  L l(p1,p2);

  int q;
  cin>>q;
  for(int i=0;i<q;i++){
    double x,y;
    cin>>x>>y;
    P p(x,y);
    P ans=projection(l,p);
    cout<<shosu(10)<<ans.real()<<"  "<<ans.imag()<<endl;

  }

}

