  int q;
  cin >> q;
  rep(i, q) {
    double x, y;
    cin >> x >> y;

    const Vec v = line.projection(Vec({x, y}));
    cout << fixed << setprecision(10) << v[0] << " " << v[1] << "\n";
  }
  cout.flush();
  return 0;
}

