class P {
public:
    P(double _x, double _y): x(_x), y(_y) {}
    P(): x(0), y(0) {}
    double x;
    double y;
    P operator-(P p) {
        P point;
        point.x = x - p.x;
        point.y = y - p.y;
        return point;
    }
    P operator+(P p) {
        P point;
        point.x = x + p.x;
        point.y = y + p.y;
        return point;
    }
    P operator*(double k) {
        P point;
        point.x = x * k;
        point.y = y * k;
        return point;
    }
    double dot(P p) {
        return x * p.x + y * p.y;
    }
    double length() {
        return sqrt(x * x + y * y);
    }
};

P projection(P p1, P p2, P p) {
    P v1 = p - p1;
    P v2 = p2 - p1;
    double t = v1.dot(v2) / v2.length() / v2.length();
    return v2 * t + p1;
}

