template<class T>
struct Vec2
{
	Vec2(){}
	Vec2(T _x, T _y) : x(_x), y(_y) {}

	Vec2 operator+(const Vec2& rhs) const { return Vec2(x + rhs.x, y + rhs.y); }
	Vec2 operator-(const Vec2& rhs) const { return Vec2(x - rhs.x, y - rhs.y); }
	Vec2 operator*(T s) const { return Vec2(x*s, y*s); }
	Vec2 operator/(T s) const { return Vec2(x/s, y/s); }
	T dot(const Vec2& rhs) const { return x*rhs.x + y*rhs.y; }
	T cross(const Vec2& rhs) const { return x*rhs.y - y*rhs.x; }
	T sqlength() const { return x*x + y*y; }
	double length() const { return sqrt(sqlength()); }
	bool operator<(const Vec2& rhs) const
	{
		if (x != rhs.x) return x < rhs.x;
		return y < rhs.y;
	}

	T x;
	T y;
};

int main()
{
	typedef Vec2<double> Vec;
	Vec P[3];
	cin >> P[0].x >> P[0].y;
	cin >> P[1].x >> P[1].y;

	int q;
	cin >> q;
	for (int i = 0; i < q; i++) {
		cin >> P[2].x >> P[2].y;

		Vec d1 = P[1] - P[0];
		Vec d2 = P[2] - P[0];
		Vec prj = d1 * d1.dot(d2) / d1.sqlength();
		Vec ans = (prj - d2) + P[2];
		printf("%.10f %.10f\n", ans.x, ans.y);
	}
	return 0;
}

